import Tone from 'tone'

function distortion() {
  return new Tone.Distortion({
    distortion: 0,
    oversample: '4x'
  })
}

let tremolo = new Tone.Tremolo({
  frequency: 10,
  type: 'sine',
  depth: 100,
  spread: 180
})

let feedbackDelay = new Tone.FeedbackDelay({
  delayTime: '8n',
  maxDelay: 10
})

let autoFilter = new Tone.AutoFilter({
  frequency: 1,
  type: 'sine',
  depth: 1,
  baseFrequency: 20,
  octaves: 4.6,
  filter: {
    type: 'lowpass',
    rolloff: -12,
    Q: 1
  }
})

function autoPanner() {
  return new Tone.AutoPanner({
    frequency: 1,
    type: 'square',
    depth: 1
  })
}

let autoWah = new Tone.AutoWah({
  baseFrequency: 100,
  octaves: 6,
  sensitivity: 0,
  Q: 2,
  gain: 2,
  follower: {
    attack: 0.3,
    release: 0.5
  }
})
function bitCrusher() {
  return new Tone.BitCrusher({
    bits: 0
  })
}
function chebyshev() {
  return new Tone.Chebyshev({
    order: 1,
    oversample: 'none'
  })
}

let chorus = new Tone.Chorus({
  frequency: 1.5,
  delayTime: 3.5,
  depth: 0.7,
  type: 'sine',
  spread: 180
})

let convolver = new Tone.Convolver({
  onload: Tone.noOp,
  normalize: true
})

// let effect = new Tone.Effect({
//   wet: 1
// })

let feedbackEffect = new Tone.FeedbackEffect({
  feedback: 0.125
})

let freeverb = new Tone.Freeverb({
  roomSize: 0.7,
  dampening: 3000
})

let jcReverb = new Tone.JCReverb({
  roomSize: 0.5
})

function phaser() {
  return new Tone.Phaser({
    frequency: 0,
    octaves: 1,
    stages: 10,
    Q: 10,
    baseFrequency: 350
  })
}

function pingPongDelay() {
  return new Tone.PingPongDelay({
    delayTime: 0.01,
    maxDelayTime: 1
  })
}

let pitchShift = new Tone.PitchShift({
  pitch: 0,
  windowSize: 0.1,
  delayTime: 0,
  feedback: 0
})

let reverb = new Tone.Reverb({
  decay: 1.5,
  preDelay: 0.01
})

let stereoWidener = new Tone.StereoWidener({
  width: 0.5
})

let vibrato = new Tone.Vibrato({
  maxDelay: 0.005,
  frequency: 5,
  depth: 0.1,
  type: 'sine'
})

autoFilter.wet.value = 0

autoWah.wet.value = 0

chorus.wet.value = 0
convolver.wet.value = 0

feedbackDelay.wet.value = 0
feedbackEffect.wet.value = 0
freeverb.wet.value = 0
jcReverb.wet.value = 0

pitchShift.wet.value = 0
reverb.wet.value = 0
stereoWidener.wet.value = 0
tremolo.wet.value = 0
vibrato.wet.value = 0

export {
  autoFilter,
  autoPanner,
  autoWah,
  bitCrusher,
  chebyshev,
  chorus,
  convolver,
  distortion,
  feedbackDelay,
  feedbackEffect,
  freeverb,
  jcReverb,
  phaser,
  pingPongDelay,
  pitchShift,
  reverb,
  stereoWidener,
  tremolo,
  vibrato
}
