import Tone from 'tone'

function seq1(synth) {
  let seq = new Tone.Sequence(
    function(time, note) {
      console.log(time, note)
      //straight quater notes
      synth.triggerAttackRelease(note, '4t', time)
    },
    ['G3', 'Cb4', 'D4', 'G4', 'D4', 'Cb4', 'G3', 'Cb4'],
    '32n'
  )
  return seq
}

function seq2(synth) {
  let seq = new Tone.Sequence(
    function(time, note) {
      console.log(time, note)
      //straight quater notes
      synth.triggerAttackRelease(note, '16n', time)
    },
    ['C4', 'Eb4', 'D4', 'Gb4', 'Bbb4'],
    '4n'
  )
  return seq
}

function seqdrum(membraneSynth) {
  let seq = new Tone.Sequence(
    function(time, note) {
      membraneSynth.triggerAttackRelease(note, '10hz', time)
    },
    [
      'G0',
      null,
      null,
      'G0',
      null,
      null,
      null,
      'G0',
      null,
      null,
      null,
      null,
      null,
      'G0',
      'G0',
      'G0'
    ],
    '16n'
  )
  return seq

  // Tone.Transport.start ();
}

function seqSnare(noiseSynth, metalSynth, membraneSynth) {
  let seq = new Tone.Sequence(
    function(time, note) {
      metalSynth.frequency.setValueAtTime(note, time)
      metalSynth.triggerAttack(time, Math.random() * 0.5 + 0.5)
      noiseSynth.triggerAttackRelease('16n', time, 1)
      membraneSynth.triggerAttackRelease('E1', '16n', time, 1)
    },
    [
      null,
      null,
      null,
      null,
      null,
      null,
      15,
      null,
      null,
      null,
      null,
      null,
      15,
      null,
      null,
      null
    ],
    '16n'
  )
  return seq

  // Tone.Transport.start ();
}

function seqPerc(membraneSynth) {
  let seq = new Tone.Sequence(
    function(time, note) {
      console.log(time, note)
      membraneSynth.triggerAttackRelease(note, '10hz', time)
    },
    ['G5', [null, 'G5'], null, 'Bb5', 'C4'],
    '4n'
  )
  return seq

  // Tone.Transport.start ();
}

export { seq1, seq2, seqdrum, seqSnare, seqPerc }
