    //this.generateRandom()

    let { name, effect, wet, on } = this.state.ambientDistortion

    effect.wet.value = on == true ? this.props.wet : 0
    effect.distortion = this.props.distortion
    effect.oversample = this.props.oversample

    this.setState({
      ambientDistortion: {
        name,
        effect,
        wet: this.props.wet,
        on
      }
    })
  }

