import _ from 'lodash'
import React from 'react'
import Tone from 'tone'

import * as effects from '../components/tunes/effects'
import * as synths from '../components/tunes/synths'

import PlaySwitch from '../components/PlaySwitch'
import Distortion from '../components/effects/Distortion'
// import FeedbackDelay from '../components/effects/FeedbackDelay'
import Chebyshev from '../components/effects/Chebyshev'
import Phaser from '../components/effects/Phaser'
import PingPongDelay from '../components/effects/PingPongDelay'
import BitCrusher from '../components/effects/BitCrusher'
import AutoPanner from '../components/effects/AutoPanner'
import ButtonSet from '../components/controls/ButtonSet'

export default class Synth extends React.Component {
  constructor(props) {
    super(props)

    const defaultWetValue = 0.8

    let ambientMachine = {
      synth: fmsynth,
      pingPongDelay: {
        effect: effects.pingPongDelay(),
        wet: defaultWetValue,
        on: false
      },
      chebyshev: {
        effect: effects.chebyshev(),
        wet: defaultWetValue,
        on: false
      },
      phaser: {
        effect: effects.phaser(),
        wet: defaultWetValue,
        on: false
      },
      bitCrusher: {
        effect: effects.bitCrusher(),
        wet: defaultWetValue,
        on: false
      },
      autoPanner: {
        effect: effects.autoPanner(),
        wet: defaultWetValue,
        on: false
      },
      distortion: {
        effect: effects.distortion(),
        wet: defaultWetValue,
        on: false
      }
    }

    let leadMachine = {
      synth: synth,
      distortion: {
        effect: effects.distortion(),
        wet: defaultWetValue,
        on: false
      }
    }

    ambientMachine.synth.chain(
      ambientMachine.autoFilter,
      ambientMachine.pingPongDelay,
      ambientMachine.chebyshev,
      ambientMachine.phaser,
      ambientMachine.bitCrusher,
      ambientMachine.autoPanners,
      ambientMachine.distortion,
      Tone.Master
    )

    leadMachine.synth.chain(leadMachine.distortion, Tone.Master)

    // Loops
    let loop1 = new Tone.Pattern(function(time, note) {
      leadMachine.synth.triggerAttackRelease(
        ['C6', 'C3', 'F4', 'A2', 'C1', 'F1'],
        '4n',
        time
      )
    }, '4n')

    let loop2 = new Tone.Loop(function(time) {
      ambientMachine.synth.triggerAttackRelease('E1', '4n', time)
    }, '4n')

    let loop3 = new Tone.Loop(function(time) {
      leadMachine.synth.triggerAttackRelease('E2', '16n', time)
    }, '2n')

    let loop4 = new Tone.Loop(function(time) {
      leadMachine.synth.triggerAttackRelease('F1', '16n', time)
    }, '16n')
    //

    //play the loop between 0-2m on the transport

    //
    // var pattern = new Tone.Pattern(
    //   function(time, note) {},
    //   ['C2', 'D4', 'E5', 'A6'],
    //   'upDown'
    // )

    this.state = {
      lastChange: Date.now(),
      timeout: 100,

      instruments: {
        ambientMachine,
        leadMachine
      },

      synth: {
        instrument: synths.synth,
        on: false
      },

      loop1: { loop: loop1, on: false },
      loop2: { loop: loop2, on: false },
