import classnames from 'classnames'
import React from 'react'

import PlaySwitch from '../controls/PlaySwitch'
import ToggleSwitch from '../controls/ToggleSwitch'
import Slider from '../controls/Slider'
import Knob from '../controls/Knob'
import ButtonSet from '../controls/ButtonSet'

export default class AutoFilter extends React.Component {
  constructor(props) {
    super(props)
  }

  render() {
    const typeSet = ['sine', 'square', 'triangle', 'sawtooth']
    const { option } = this.props

    // const classes = classnames({
    //   sawtooth: typeSet == 'sawtooth'
    // })
    const classes = classnames({
      typeSet: 'sawtooth',
      sawtooth: option == current
    })

    // let name = 'bitCrusher'
    const {
      name,
      effect,
      wet,
      on,
      toggleEffect,
      changeEffectWetValue,
      changeEffectValue
    } = this.props

    return (
      <div>
        <div className="Pannel">
          <div className="controlsContainer">
            <div className="controlRow">
              <ToggleSwitch
                value="AutoFilter"
                current={on}
                handleClick={toggleEffect}
              />
              <div className="EffectCover">
                <h2>Wet</h2>
                <div className="SliderStick"></div>
                <Slider
                  name={name}
                  property="wet"
                  min="0"
                  max="1"
                  value={wet}
                  handleValueChange={changeEffectWetValue}
                />
              </div>

              <div className="controlRow">
                <h2>Depth</h2>
                <div className="SliderStick"></div>
                <Slider
                  name={name}
                  min="-3"
                  max="3"
                  value={effect.depth}
                  handleValueChange={changeEffectValue}
                />
              </div>

              <h2>Type</h2>
              <ButtonSet
                className={classes}
                name={name}
                property="type"
                value={effect.type}
                handleValueChange={changeEffectValue}
              />
              {option}
            </div>
          </div>
        </div>
      </div>
    )
  }
}
