import Tone from 'tone'
// SYNTH
function polySynth() {
  return new Tone.PolySynth(1, Tone.Synth, {
    envelope: {
      attack: 0.01,
      attackCurve: 'linear',
      decay: 0.05,
      sustain: 0.2,
      release: 0.4,
      releaseCurve: 'bounce'
    },
    oscillator: {
      type: 'pulse',
      width: 0.8
    }
  })
}

function tSynth() {
  return new Tone.Synth({
    portamento: 0.2,
    envelope: {
      attack: 0.1,
      decay: 0.6,
      sustain: 0,
      release: 1.6
    },
    oscillator: {
      type: 'fat',
      partialCount: 5,
      spread: 40,
      count: 3
    }
  })
}
//   {
//   harmonicity: 10,
