////список все эффектов

import _ from 'lodash'
import React from 'react'
import Tone from 'tone'

export default class Synth extends React.Component {
  constructor(props) {
    super(props)

    let distortion = new Tone.Distortion({
      distortion: 100,
      oversample: '4x'
    })

    let tremolo = new Tone.Tremolo({
      frequency: 10,
      type: 'sine',
      depth: 100,
      spread: 180
    })

    let feedbackDelay = new Tone.FeedbackDelay({
      delayTime: '8n',
      maxDelay: 10
    })

    let autoFilter = new Tone.AutoFilter({
      frequency: 1,
      type: 'sine',
      depth: 1,
      baseFrequency: 20,
      octaves: 4.6,
      filter: {
        type: 'lowpass',
        rolloff: -12,
        Q: 1
      }
    })

    let distortion100 = new Tone.Distortion({
      distortion: 100,
      oversample: '4x'
    })

    let autoPanner = new Tone.AutoPanner({
      frequency: 1,
      type: 'sine',
      depth: 1
    })

    let autoWah = new Tone.AutoWah({
      baseFrequency: 100,
      octaves: 6,
      sensitivity: 0,
      Q: 2,
      gain: 2,
      follower: {
        attack: 0.3,
        release: 0.5
      }
    })

    let bitCrusher = new Tone.BitCrusher({
      bits: 4
    })

    let chebyshev = new Tone.Chebyshev({
      order: 50,
      oversample: 'none'
    })

    let chorus = new Tone.Chorus({
      frequency: 1.5,
      delayTime: 3.5,
      depth: 0.7,
      type: 'sine',
      spread: 180
    })

    let convolver = new Tone.Convolver({
      onload: Tone.noOp,
      normalize: true
    })

    // let effect = new Tone.Effect({
    //   wet: 1
    // })

    var feedbackEffect = new Tone.FeedbackEffect({
      feedback: 0.125
    })

    var freeverb = new Tone.Freeverb({
      roomSize: 0.7,
      dampening: 3000
    })

    var jcReverb = new Tone.JCReverb({
      roomSize: 0.5
    })

    let phaser = new Tone.Phaser({
      frequency: 0.5,
      octaves: 3,
      stages: 10,
      Q: 10,
      baseFrequency: 350
    })

    let pingPongDelay = new Tone.PingPongDelay({
      delayTime: 0.25,
      maxDelayTime: 1
    })

    let pitchShift = new Tone.PitchShift({
      pitch: 0,
      windowSize: 0.1,
      delayTime: 0,
      feedback: 0
    })

    let reverb = new Tone.Reverb({
      decay: 1.5,
      preDelay: 0.01
    })

    let stereoWidener = new Tone.StereoWidener({
      width: 0.5
    })

    let vibrato = new Tone.Vibrato({
      maxDelay: 0.005,
      frequency: 5,
      depth: 0.1,
      type: 'sine'
    })

    feedbackDelay.wet.value = 0
    tremolo.wet.value = 0
    distortion.wet.value = 0
    autoPanner.wet.value = 0
    autoWah.wet.value = 0
    bitCrusher.wet.value = 0
    chebyshev.wet.value = 0
    chorus.wet.value = 0
    convolver.wet.value = 0
    effect.wet.value = 0
    feedbackEffect.wet.value = 0
    freeverb.wet.value = 0
    jcReverb.wet.value = 0
    phaser.wet.value = 0
    pingPongDelay.wet.value = 0
    pitchShift.wet.value = 0
    reverb.wet.value = 0
    stereoWidener.wet.value = 0
    vibrato.wet.value = 0

    // SYNTH
    let synth = new Tone.Synth()

    synth.chain(
      feedbackDelay,
      tremolo,
      distortion,
      autoPanner,
      autoFilter,
      autoWah,
      bitCrusher,
      chebyshev,
      chorus,
      convolver,
      effect,
      feedbackEffect,
      freeverb,
      jcReverb,
      phaser,
      pingPongDelay,
      pitchShift,
      reverb,
      stereoWidener,
      vibrato,

      Tone.Master
    )

    this.state = {
      feedbackDelay,
      tremolo,
      distortion,
      autoPanner,
      autoFilter,
      autoWah,
      bitCrusher,
      chebyshev,
      chorus,
      convolver,
      effect,
      feedbackEffect,
      freeverb,
      jcReverb,
      phaser,
      pingPongDelay,
      pitchShift,
      reverb,
      stereoWidener,
      vibrato,
      synth
    }

    this.startSynth = this.startSynth.bind(this)
    this.toggleDistortion = this.toggleDistortion.bind(this)

    // _.bindAll(this, 'startSynth', 'toggleDistortion100')
    //перебиндить все таким образом
  }

  startSynth() {
    let { synth } = this.state

    let loop = new Tone.Loop(function(time) {
      synth.triggerAttackRelease('C2', '8n', time)
    }, '4n')

    loop.start('0m').stop('16m')

    Tone.Transport.bpm.value = 115
    Tone.Transport.start()
  }

  toggleDistortion() {
    let { distortion } = this.state
    distortion.wet.value = 1

    this.setState({
      distortion
    })
  }

  render() {
    return (
      <div>
        <div onClick={this.startSynth}>Start</div>
        <div onClick={this.toggleDistortion}>toggle Distortion</div>
      </div>
    )
  }
}
