import Tone from 'tone'

function part1(synth) {
  let part = new Tone.Part(
    function(time, note) {
      synth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    },
    [
      {
        time: '0:0:0',
        noteName: 'B1',
        velocity: 1,
        duration: '1n'
      },
      {
        time: '1:0:1',
        noteName: 'A1',
        velocity: 1,
        duration: '1n'
      },
      {
        time: '1:2:0',
        noteName: 'C2',
        velocity: 1,
        duration: '1n'
      }
      // {
      //   time: '1:1:0',
      //   noteName: 'B1',
      //   velocity: 0,
      //   duration: '1n'
      // }
      // {
      //   time: '0:3:0',
      //   noteName: 'B1',
      //   velocity: 1,
      //   duration: '2n'
      // }
    ]
  )
  part.loop = true
  part.loop = '4m'

  return part
}

function part2(synth) {
  let part = new Tone.Part(
    function(time, note) {
      synth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    },
    [
      {
        time: '0:0:0',
        noteName: 'c2',
        velocity: 1,
        duration: '8n'
      },
      {
        time: '0:1:0',
        noteName: 'C3',
        velocity: 1,
        duration: '16n'
      },
      {
        time: '0:2:0',
        noteName: 'g2',
        velocity: 1,
        duration: '8n'
      }
      // {
      //   time: '0:3:0',
      //   noteName: 'A4',
      //   velocity: 1,
      //   duration: '16n'
      // },
      // {
      //   time: '0:4:0',
      //   noteName: 'Bb3',
      //   velocity: 1,
      //   duration: '16n'
      // }
    ]
  )

  part.loop = true
  part.loop = '8m'

  return part
}

export { part1, part2 }
