import Tone from 'tone'
// SYNTH
function polySynth() {
  return new Tone.PolySynth(1, Tone.Synth, {
    envelope: {
      attack: 0.01,
      attackCurve: 'linear',
      decay: 0.05,
      sustain: 0.2,
      release: 0.4,
      releaseCurve: 'bounce'
    },
    oscillator: {
      type: 'pulse',
      width: 0.8
    }
  })
}

function tSynth() {
  return new Tone.Synth({
    // portamento: 0.2,
    envelope: {
      attack: 0.01,
      attackCurve: 'linear',
      decay: 1.6,
      decayCurve: 'exponential',
      sustain: 0,
      release: 1.6,
      releaseCurve: 'exponential'
    }
    // oscillator: {
    //   type: 'square',
    //   partialCount: 0,
    //   spread: 40,
    //   count: 3
    // }
  })
}

function amSynth() {
  return new Tone.AMSynth({
    portamento: 0,
    harmonicity: 2.4,
    carrierEnvelope: {
      attack: 0.2,
      attackCurve: 'linear',
      decay: 0.11,
      decayCurve: 'exponental',
      sustain: 0.3,
      release: 2.84,
      releaseCurve: 'exponental'
    },
    carrierOscillator: {
      type: 'sawtooth'
    },
    modulationEnvelop: {
      attack: 0.1,
      attackCurve: 'linear',
      decay: 1.17,
      decayCurve: 'exponental',
      sustain: 1,
      release: 2.15,
      releaseCurve: 'exponental'
    },
    modulationOscillator: {
      type: 'triangle'
    }
  })
}

function metalSynth() {
  return new Tone.MetalSynth({
    harmonicity: 12,
    modulationIndex: 21,
    resonance: 106,
    octaves: 4,

    envelope: {
      attack: 0.01,
      attackCurve: 'linear',
      decay: 0.04,
      decayCurve: 'linear',
      sustain: 0,
      release: 0.49,
      releaseCurve: 'exponental'
    }
  })
}

function drumSynth() {
  return new Tone.MembraneSynth({
    pitchDecay: 0.05,
    octaves: 10,
    oscillator: {
      type: 'sine'
    },
    envelope: {
      attack: 0.001,
      decay: 0.4,
      sustain: 0.01,
      release: 1.4,
      attackCurve: 'exponential'
    }
  })
}

export { polySynth, tSynth, amSynth, metalSynth, drumSynth }
