import Tone from 'tone'

function seq1(synth) {
  let seq = new Tone.Sequence(
    function(time, note) {
      console.log(time, note)
      //straight quater notes
      synth.triggerAttackRelease(note, '10hz', time)
    },
    ['A0 C1 E1', 'F0 A0 C1', 'G0 B0 D1', 'D0 F0 A0', 'E0 G0 B0']
  )
  return seq
}

export { seq1 }
