import Tone from 'tone'
// SYNTH
function polySynth() {
  return new Tone.PolySynth(1, Tone.AMSynth, {
    envelope: {
      attack: 0.01,
      attackCurve: 'exponential',
      decay: 0.1,
      decayCurve: 'exponential',
      sustain: 0.5,
      release: 0.4,
      releaseCurve: 'exponential'
    },
    oscillator: {
      type: 'pulse',
      spread: 30,
      count: 3
    }
  })
}

function tSynth() {
