      decay: 0.1,
      sustain: 0.3,
      release: 1
    },
    oscillator: {
      type: 'triangle'
    }
  })
}

function amSynth() {
  return new Tone.AMSynth({
    portamento: 0,
    harmonicity: 2.4,
    carrierEnvelope: {
      attack: 0.2,
      attackCurve: 'linear',
      decay: 0.11,
      decayCurve: 'exponental',
