import React from 'react'
import Tone from 'tone'

export default class Synth extends React.Component {
  constructor(props) {
    super(props)

    let distortion = new Tone.Distortion({
      distortion: 1,
      oversample: '4x'
    })

    let tremolo = new Tone.Tremolo({
      frequency: 10,
      type: 'sine',
      depth: 100,
      spread: 180
    })

    let feedbackDelay = new Tone.FeedbackDelay({
      delayTime: '8n',
      maxDelay: 10
    })

    let autoFilter = new Tone.AutoFilter({
      frequency: 1,
      type: 'sine',
      depth: 1,
      baseFrequency: 20,
      octaves: 4.6,
      filter: {
        type: 'lowpass',
        rolloff: -12,
        Q: 1
      }
    })

    let distortion100 = new Tone.Distortion({
      distortion: 100,
      oversample: '4x'
    })

    let autoPanner = new Tone.AutoPanner({
      frequency: 1,
      type: 'sine',
      depth: 1
    })

    let autoWah = new Tone.AutoWah({
      baseFrequency: 100,
      octaves: 6,
      sensitivity: 0,
      Q: 2,
      gain: 2,
      follower: {
        attack: 0.3,
        release: 0.5
      }
    })

    let bitCrusher = new Tone.BitCrusher({
      bits: 4
    })

    let chebyshev = new Tone.Chebyshev({
      order: 50,
      oversample: 'none'
    })

    let chorus = new Tone.Chorus({
      frequency: 1.5,
      delayTime: 3.5,
      depth: 0.7,
      type: 'sine',
      spread: 180
    })

    let convolver = new Tone.Convolver({
      onload: Tone.noOp,
      normalize: true
    })

    let effect = new Tone.Effect({
      wet: 1
    })

    var feedbackEffect = new Tone.FeedbackEffect({
      feedback: 0.125
    })

    var freeverb = new Tone.Freeverb({
      roomSize: 0.7,
      dampening: 3000
    })

    var jcReverb = new Tone.JCReverb({
      roomSize: 0.5
    })

    let phaser = new Tone.Phaser({
      frequency: 0.5,
      octaves: 3,
      stages: 10,
      Q: 10,
      baseFrequency: 350
    })

    let pingPongDelay = new Tone.PingPongDelay({
      delayTime: 0.25,
      maxDelayTime: 1
    })

    let pitchShift = new Tone.PitchShift({
      pitch: 0,
      windowSize: 0.1,
      delayTime: 0,
      feedback: 0
    })

    let reverb = new Tone.Reverb({
      decay: 1.5,
      preDelay: 0.01
    })

    let stereoWidener = new Tone.StereoWidener({
      width: 0.5
    })

    let vibrato = new Tone.Vibrato({
      maxDelay: 0.005,
      frequency: 5,
      depth: 0.1,
      type: 'sine'
    })

    this.state = {
      distortion: distortion,
      distortionIsOn: false,
      tremolo: tremolo,
      tremoloIsOn: false,
      feedbackDelay: feedbackDelay,
      feedbackDelayIsOn: false,
      autoFilter: autoFilter,
      autoFilterIsOn: false,
      distortion100: distortion100,
      distortion100IsOn: false,
      autoPanner: autoPanner,
      autoPannerIsOn: false,

      autoWah: autoWah,
      autoWahIsOn: false,
      bitCrusher: bitCrusher,
      bitCrusherIsOn: false,
      chebyshev: chebyshev,
      chebyshevIsOn: false,
      chorus: chorus,
      chorusIsOn: false,
      convolver: convolver,
      convolverIsOn: false,
      effect: effect,
      effectIsOn: false,
      feedbackEffect: feedbackEffect,
      feedbackEffectIsOn: false,
      freeverb: freeverb,
      freeverbIsOn: false,
      jcReverb: jcReverb,
      jcReverbIsOn: false,
      phaser: phaser,
      phaserIsOn: false,
      pingPongDelay: pingPongDelay,
      pingPongDelayIsOn: false,
      pitchShift: pitchShift,
      pitchShiftIsOn: false,
      reverb: reverb,
      reverbIsOn: false,
      stereoWidener: stereoWidener,
      stereoWidenerIsOn: false,
      vibrato: vibrato,
      vibratoIsOn: false
    }

    this.startSynth = this.startSynth.bind(this)
    this.toggleFilter = this.toggleFilter.bind(this)
    this.toggleFeedbackDelay = this.toggleFeedbackDelay.bind(this)
    this.toggleTremolo = this.toggleTremolo.bind(this)
    this.toggleDistortion = this.toggleDistortion.bind(this)
    this.toggleDistortion100 = this.toggleDistortion100.bind(this)
    this.toggleAutoPanner = this.toggleAutoPanner.bind(this)
    this.toggleAutoWah = this.toggleAutoWah.bind(this)

    this.toggleAutoWah = this.toggleAutoWah.bind(this)
    this.toggleBitCrusher = this.toggleBitCrusher.bind(this)
    this.toggleChebyshev = this.toggleChebyshev.bind(this)
    this.toggleChorus = this.toggleChorus.bind(this)
    this.toggleConvolver = this.toggleConvolver.bind(this)
    this.toggleEffect = this.toggleEffect.bind(this)
    this.toggleFeedbackEffect = this.toggleFeedbackEffect.bind(this)
    this.toggleFreeverb = this.toggleFreeverb.bind(this)
    this.toggleBitCrusher = this.toggleBitCrusher.bind(this)
    this.toggleJCReverb = this.toggleJCReverb.bind(this)
    this.togglePhaser = this.togglePhaser.bind(this)
    this.togglePingPongDelay = this.togglePingPongDelay.bind(this)
    this.togglePitchShift = this.togglePitchShift.bind(this)
    this.toggleReverb = this.toggleReverb.bind(this)
    this.toggleStereoWidener = this.toggleStereoWidener.bind(this)
    this.toggleVibrato = this.toggleVibrato.bind(this)
  }

  startSynth() {
    let synth = new Tone.Synth()
    synth.chain(
      this.state.autoFilter,
      this.state.feedbackDelay,
      this.state.tremolo,
      this.state.distortion,
      this.state.distortion100,
      this.state.autoPanner,
      this.state.autoWah,
      this.state.bitCrusher,
      this.state.chebyshev,
      this.state.chorus,
      this.state.convolver,
      this.state.effect,
      this.state.feedbackEffect,
      this.state.freeverb,
      this.state.jcReverb,
      this.state.phaser,
      this.state.pingPongDelay,
      this.state.pitchShift,
      this.state.reverb,
      this.state.stereoWidener,
      this.state.vibrato,

      Tone.Master
    )

    this.setState({
      synth: synth
    })

    let loop = new Tone.Loop(function(time) {
      synth.triggerAttackRelease('C2', '8n', time)
    }, '4n')

    loop.start('0m').stop('16m')

    Tone.Transport.bpm.value = 115
    Tone.Transport.start()
  }

  toggleFilter() {
    if (this.state.autoFilterIsOn == true) {
      this.state.autoFilter.wet.value = 0

      this.setState({
        autoFilterIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.autoFilter, Tone.Master)
      this.state.autoFilter.wet.value = 1

      this.setState({
        autoFilterIsOn: true
      })
    }
  }

  toggleFeedbackDelay() {
    // let feedbackDelay1 = new Tone.FeedbackDelay({
    //   delayTime: '32n',
    //   maxDelay: 10
    // })
    //
    // let feedbackDelay2 = new Tone.FeedbackDelay({
    //   delayTime: '8n',
    //   maxDelay: 10
    // })
    //
    // this.state.synth.connect(feedbackDelay1)
    // this.state.synth.connect(feedbackDelay2)
    if (this.state.feedbackDelayIsOn == true) {
      this.state.feedbackDelay.wet.value = 0

      this.setState({
        feedbackDelayIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.feedbackDelay, Tone.Master)
      this.state.feedbackDelay.wet.value = 1

      this.setState({
        feedbackDelayIsOn: true
      })
    }
  }

  toggleTremolo() {
    if (this.state.tremoloIsOn == true) {
      this.state.tremolo.wet.value = 0

      this.setState({
        tremoloIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.tremolo, Tone.Master)
      this.state.tremolo.wet.value = 1

      this.setState({
        tremoloIsOn: true
      })
    }
  }

  toggleDistortion() {
    if (this.state.distortionIsOn == true) {
      // this.state.synth.disconnect()
      // this.state.synth.chain(Tone.Master) строчка ниже заменяет

      this.state.distortion.wet.value = 0

      this.setState({
        distortionIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.distortion, Tone.Master)
      this.state.distortion.wet.value = 1

      this.setState({
        distortionIsOn: true
      })
    }
    //this.state.synth.connect(distortion)
  }

  toggleDistortion100() {
    // let { distortion } = this.state
    // distortion.distortion = 100
    //
    // this.setState({
    //   distortion: distortion
    // })
    if (this.state.distortion100IsOn == true) {
      this.state.distortion100.wet.value = 0

      this.setState({
        distortion100IsOn: false
      })
    } else {
      this.state.synth.chain(this.state.distortion100, Tone.Master)
      this.state.distortion100.wet.value = 1

      this.setState({
        distortion100IsOn: true
      })
    }
  }

  toggleAutoPanner() {
    if (this.state.autoPannerIsOn == true) {
      this.state.autoPanner.wet.value = 0

      this.setState({
        autoPannerIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.autoPanner, Tone.Master)
      this.state.autoPanner.wet.value = 1

      this.setState({
        autoPannerIsOn: true
      })
    }
  }

  toggleAutoWah() {
    if (this.state.autoWahIsOn == true) {
      this.state.autoWah.wet.value = 0

      this.setState({
        autoWahIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.autoWah, Tone.Master)
      this.state.autoWah.wet.value = 1

      this.setState({
        autoWahIsOn: true
      })
    }
  }

  toggleBitCrusher() {
    if (this.state.bitCrusherIsOn == true) {
      this.state.bitCrusher.wet.value = 0

      this.setState({
        bitCrusherIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.bitCrusher, Tone.Master)
      this.state.bitCrusher.wet.value = 1

      this.setState({
        bitCrusherIsOn: true
      })
    }
  }

  toggleChebyshev() {
    if (this.state.chebyshevIsOn == true) {
      this.state.chebyshev.wet.value = 0

      this.setState({
        chebyshevIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.chebyshev, Tone.Master)
      this.state.chebyshev.wet.value = 1

      this.setState({
        chebyshevIsOn: true
      })
    }
  }

  toggleChorus() {
    if (this.state.chorusIsOn == true) {
      this.state.chorus.wet.value = 0

      this.setState({
        chorusIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.chorus, Tone.Master)
      this.state.chorus.wet.value = 1

      this.setState({
        chorusIsOn: true
      })
    }
  }

  toggleConvolver() {
    if (this.state.convolverIsOn == true) {
      this.state.convolver.wet.value = 0

      this.setState({
        convolverIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.convolver, Tone.Master)
      this.state.convolver.wet.value = 1

      this.setState({
        convolverIsOn: true
      })
    }
  }

  toggleEffect() {
    if (this.state.effectIsOn == true) {
      this.state.effect.wet.value = 0

      this.setState({
        effectIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.effect, Tone.Master)
      this.state.effect.wet.value = 1

      this.setState({
        effectIsOn: true
      })
    }
  }

  toggleFeedbackEffect() {
    if (this.state.feedbackEffectIsOn == true) {
      this.state.feedbackEffect.wet.value = 0

      this.setState({
        feedbackEffectIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.feedbackEffect, Tone.Master)
      this.state.feedbackEffect.wet.value = 1

      this.setState({
        feedbackEffectIsOn: true
      })
    }
  }

  toggleFreeverb() {
    if (this.state.freeverbIsOn == true) {
      this.state.freeverb.wet.value = 0

      this.setState({
        freeverbIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.freeverb, Tone.Master)
      this.state.freeverb.wet.value = 1

      this.setState({
        freeverbIsOn: true
      })
    }
  }

  toggleJCReverb() {
    if (this.state.jcReverbIsOn == true) {
      this.state.jcReverb.wet.value = 0

      this.setState({
        jcReverbIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.jcReverb, Tone.Master)
      this.state.jcReverb.wet.value = 1

      this.setState({
        jcReverbIsOn: true
      })
    }
  }

  togglePhaser() {
    if (this.state.phaserIsOn == true) {
      this.state.phaser.wet.value = 0

      this.setState({
        phaserIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.phaser, Tone.Master)
      this.state.phaser.wet.value = 1

      this.setState({
        phaserIsOn: true
      })
    }
  }

  togglePingPongDelay() {
    if (this.state.pingPongDelayIsOn == true) {
      this.state.pingPongDelay.wet.value = 0

      this.setState({
        pingPongDelayIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.pingPongDelay, Tone.Master)
      this.state.pingPongDelay.wet.value = 1

      this.setState({
        pingPongDelayIsOn: true
      })
    }
  }

  togglePitchShift() {
    if (this.state.pitchShiftIsOn == true) {
      this.state.pitchShift.wet.value = 0

      this.setState({
        pitchShiftIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.pitchShift, Tone.Master)
      this.state.pitchShift.wet.value = 1

      this.setState({
        pitchShiftIsOn: true
      })
    }
  }

  toggleReverb() {
    if (this.state.reverbIsOn == true) {
      this.state.reverb.wet.value = 0

      this.setState({
        reverbIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.reverb, Tone.Master)
      this.state.reverb.wet.value = 1

      this.setState({
        reverbIsOn: true
      })
    }
  }

  toggleStereoWidener() {
    if (this.state.stereoWidenerIsOn == true) {
      this.state.stereoWidener.wet.value = 0

      this.setState({
        stereoWidenerIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.stereoWidener, Tone.Master)
      this.state.stereoWidener.wet.value = 1

      this.setState({
        stereoWidenerIsOn: true
      })
    }
  }

  toggleVibrato() {
    if (this.state.vibratoIsOn == true) {
      this.state.vibrato.wet.value = 0

      this.setState({
        vibratoIsOn: false
      })
    } else {
      this.state.synth.chain(this.state.vibrato, Tone.Master)
      this.state.vibrato.wet.value = 1

      this.setState({
        vibratoIsOn: true
      })
    }
  }

  render() {
    return (
      <div>
        <div onClick={this.startSynth}>Start</div>
        <div onClick={this.toggleFilter}>
          toggle Filter {this.state.autoFilterIsOn}
        </div>
        <div onClick={this.toggleFeedbackDelay}>
          toggle FeedbackDelay {this.state.feedbackDelayIsOn}
        </div>
        <div onClick={this.toggleTremolo}>
          toggle Tremolo {this.state.tremoloIsOn}
        </div>
        <div onClick={this.toggleDistortion}>
          toggle Distortion {this.state.distortionIsOn}
        </div>
