        </div>
        <div onClick={this.toggleBitCrusher}>
          toggle BitCrusher {this.state.bitCrusherIsOn}
        </div>
        <div onClick={this.toggleChebyshev}>
          toggle Chebyshev {this.state.chebyshevIsOn}
        </div>
        <div onClick={this.toggleChorus}>
          toggle Chorus {this.state.chorusIsOn}
        </div>
        <div onClick={this.toggleConvolver}>
          toggle Convolver {this.state.convolverIsOn}
        </div>
        <div onClick={this.toggleEffect}>
          toggle Effect {this.state.effectIsOn}
        </div>
        <div onClick={this.toggleFeedbackEffect}>
          toggle FeedbackEffect {this.state.feedbackEffectIsOn}
        </div>
        <div onClick={this.toggleFreeverb}>
          toggle Freeverb {this.state.freeverbIsOn}
        </div>
        <div onClick={this.toggleJCReverb}>
          toggle JCReverb {this.state.jcReverbIsOn}
        </div>
        <div onClick={this.togglePhaser}>
          toggle Phaser {this.state.phaserIsOn}
        </div>
        <div onClick={this.togglePingPongDelay}>
          toggle PingPongDelay {this.state.pingPongDelayIsOn}
