import Tone from 'tone'

function seq1(synth) {
  let seq = new Tone.Sequence(
    function(time, note) {
      console.log(time, note)
      //straight quater notes
      synth.triggerAttackRelease()
    },
    ['A3', 'C4', 'E3', 'E3', 'A3', 'C4', 'A2'],
    '4n',
    {
      subdivision: '4n'
    }
  )
  return seq
}

export { seq1 }
