import React from 'react'

// import PlaySwitch from '../PlaySwitch'
import ToggleSwitch from '../ToggleSwitch'
import Slider from '../Slider'
import Knob from '../Knob'

export default class Distortion extends React.Component {
  constructor(props) {
    super(props)
  }

  render() {
    let name = 'distortion'
    const {
      effect,
      on,
      toggleEffect,
      changeEffectWetValue,
      changeDistortionValue
    } = this.props

    return (
      <div>
        <h1>DISTORTION</h1>
        <div className="Pannel">
          Change Distortion Wet Value
          <Slider
            name={name}
            min="0"
            max="1"
            value={effect.wet.value}
            handleValueChange={changeEffectWetValue}
          />
          Change Distortion Value
          <Slider
            name={name}
            min="0"
            max="100"
            value={effect.distortion}
            handleValueChange={changeDistortionValue}
          />
          Toggle Distortion
          <ToggleSwitch
            value="Distortion"
            current={on}
            handleClick={toggleEffect}
          />
        </div>
      </div>
    )
  }
}
