import React from 'react'

import PlaySwitch from '../PlaySwitch'
import ToggleSwitch from '../ToggleSwitch'
import Slider from '../Slider'
import ButtonSet from '../controls/ButtonSet'

export default class Distortion extends React.Component {
  constructor(props) {
    super(props)
  }

  render() {
    // const name = 'distortion'
    const set = ['none', '2x', '4x']

    const {
      name,
      effect,
      wet,
      on,
      toggleEffect,
      changeEffectWetValue,
      changeEffectValue,
      changeDistortionValue
    } = this.props

    return (
      <div>
        <div className="Pannel" id="blue">
          <h1>Distortion</h1>
          <div className="controlsContainer">
            <div className="controlRow">
              <h2>Wet</h2>
              <Slider
                id="1"
                name={name}
                min="0"
                max="1"
                value={wet}
                handleValueChange={changeEffectWetValue}
              />
            </div>

            <div className="controlRow">
              <h2>Distortion</h2>
              <Slider
                name={name}
                min="0"
                max="100"
                value={effect.distortion}
                handleValueChange={changeDistortionValue}
              />
            </div>
          </div>
          <ToggleSwitch
            value="Distortion"
            current={on}
            handleClick={toggleEffect}
          />

          <h2>Oversample</h2>
          <ButtonSet
            name={name}
            property="oversample"
            set={set}
            value={effect.oversample}
            handleValueChange={changeEffectValue}
          />
        </div>
      </div>
    )
  }
}
